local bg_colors = {
    background_main = Color(0, 0, 10, 200),
	noround = Color(100, 100, 100, 200)
}

net.Receive("ClientDeathNotify", function()
    local NameColor = Color(236, 240, 241)
    local White = Color(236, 240, 241)
    local name = net.ReadString()
    local role = tonumber(net.ReadString())
    local reason = net.ReadString()
	local dna = net.ReadBool()

    local col = GetRoleColor(role) or bg_colors.noround
    role = LANG.GetRawTranslation(GetRoleStringRaw(role))

	local word = terms[math.random(#terms)]
	
    if reason == "suicide" then
        chat.AddText(moat_blue, "| ", NameColor, "There are far, far better things ahead than anything this ", White, word .. " ", White, "left behind")
    elseif reason == "burned" then
		local burn = {"cooked", "tempered", "burned-out", "burnt-out"}
		chat.AddText(moat_blue, "| ", White, "Burned ", moat_red, "900 ", White, "years of time and space, this ", moat_green, word .. " ", White, "ain't ", moat_red, "never ", White, burn[math.random(#burn)] .. "")
    elseif reason == "prop" then
		local sm = prop_smashed[math.random(#prop_smashed)]
		if (not string.EndsWith(sm, "s")) then
			sm = "a " .. sm
		end

		chat.AddText(moat_blue, "| ", White, "This ", White, word .. " ", White, "just got ", White, killed_adverbs[math.random(#killed_adverbs)] .. " ", moat_cyan, "prop killed ", White, "by " .. sm .. " ", White, ".. ", White, "like props ", White, smashed_verbs[math.random(#smashed_verbs)])
    elseif reason == "ply" then
		local sm = killed_verbs[math.random(#killed_verbs)]

		if (dna) then
			chat.AddText(moat_blue, "| ", NameColor, "You", White, " were " .. sm .. " by ", col, name, White, " .. " .. terms[math.random(#terms)] .. " was a ", col, role, White, " .. They got your ", moat_cyan, "DNA Fingerprints ", White, " found from your terrorists law-breaking crimes .. gulp")
		else
			chat.AddText(moat_blue, "| ", NameColor, "You", White, " were " .. sm .. " by ", col, name, White, " .. " .. terms[math.random(#terms)] .. " was a ", col, role)
		end
    elseif reason == "fell" then
        chat.AddText(moat_blue, "| ", NameColor, "If this ", moat_pink, word .. " ", White, "was born with a weakness to fall .. ")
		chat.AddText(moat_blue, "| ", White, "Then for sure this ", moat_pink, word .. " ", White, "was born with a strength to rise")
    elseif reason == "water" then
        chat.AddText(moat_blue, "| ", White, "In ", moat_cyan, "900 ", White, "years of time and space, this ", moat_pink, word .. " ", White, "has ", moat_cyan, "never ", White, "drowned")
    else
        chat.AddText(moat_blue, "| ", White, "It was ", NameColor, "unknown ", White, "how you were killed")
    end
end)